}
handleSubmit(event)  {

     axios({
            method: "POST",
            url: `http://127.0.0.1:8000/api/form/`,
            data: {
              name: this.state.name,
              email: this.state.email,
              message: this.state.message,

            },
         headers: {
            "X-CSRFToken": "csrftoken",
            'Content-Type': 'multipart/form-data',


         },

        }).then(res => {
                 this.state.data = res.data
