    }).catch(function (error) {
        console.log(error);

    });
    event.preventDefault();


}


    render(){
        return(
            <div>
            <section class="section section-on-footer" data-background="images/backgrounds/bg-dots.png">
  <div class="container">
    <div class="row">
      <div class="col-12 text-center">
        <h2 class="section-title">Наши контакты</h2>
      </div>
      <div class="col-lg-8 mx-auto">
        <div class="bg-white rounded text-center p-5 shadow-down">
          <h4 class="mb-80">Контакная форма</h4>
          <form action="#" class="row">
            <div class="col-md-6">
              <input type="text"  onChange = {this.onChange} value = {this.state.name} id="name" name="name" placeholder="Ф.И.О." class="form-control px-0 mb-4" />
            </div>
            <div class="col-md-6">
              <input type="email" id="email" name="email"  onChange = {this.onChange} value = {this.state.email} placeholder="Email" class="form-control px-0 mb-4" />
            </div>
            <div class="col-12">
              <textarea name="message" id="message" class="form-control px-0 mb-4"
                placeholder="Ваше сообщение"></textarea>
            </div>
            <div class="col-lg-6 col-10 mx-auto">
              <button class="btn btn-primary w-100">Отправить</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  </div>
</section>

<footer class="bg-dark footer-section">
  <div class="section">
    <div class="container">
      <div class="row">
        <div class="col-md-4">
          <h5 class="text-light">Email</h5>
          <p class="text-white paragraph-lg font-secondary">elab.shakha@gmail.com</p>
        </div>
        <div class="col-md-4">
          <h5 class="text-light">Номер телефона</h5>
          <p class="text-white paragraph-lg font-secondary">+777777777</p>
        </div>
        <div class="col-md-4">
          <h5 class="text-light">Адрес</h5>
          <p class="text-white paragraph-lg font-secondary">Казахстан, г. Алматы, Ауезовский район, Аксай 5, Дом 25</p>
        </div>
      </div>
    </div>
  </div>
  <div class="border-top text-center border-dark py-5">
    <p class="mb-0 text-light">Copyright ©<script>
        var CurrentYear = new Date().getFullYear()
        document.write(CurrentYear)
      </script> by <a href="elab.asia">elab.asia</a></p>
  </div>
</footer>

</div>

)
    }
}

export default Footer;
